<?phprequire_once '../config/settings.php';// get request method$requestMethod = $_SERVER['REQUEST_METHOD'];if ($requestMethod == "POST") {    $data = cleanInputs($_POST);} else if ($requestMethod == "GET") {    $data = cleanInputs($_GET);}extract($data);$_REQUEST['action'] = $xssObj->clean_input($_REQUEST['action']);if ($_REQUEST['action'] != '') {    switch ($_REQUEST['action']) {        // USER RELATED SERVICES STARTS        case 'brandList':		   $fileInclude = 'module/brandlist.php';            break;        case 'modelList':		            $fileInclude = 'module/modelList.php';            break;       default:          $arr['error'] = "Please check your action";		   echo json_encode($arr);		   break;    }    require_once $fileInclude;} else {    $arr['error'] = "Please check your action";    echo json_encode($arr);}?>